#include <bits/stdc++.h> using namespace std; using ll = long long; const int MAX = 200010; const ll MOD = 998244353; ll fact[MAX], invfact[MAX]; ll modpow(ll b, ll e) { ll r = 1; while (e) { if (e & 1) r = r * b % MOD; b = b * b % MOD; e >>= 1; } return r; } void pre() { fact[0] = 1; for (int i = 1; i < MAX; i++) { fact[i] = fact[i - 1] * i % MOD; } invfact[MAX - 1] = modpow(fact[MAX - 1], MOD - 2); for (int i = MAX - 2; i >= 0; i--) { invfact[i] = invfact[i + 1] * (i + 1) % MOD; } } int main() { pre(); int T; cin >> T; for (int t = 0; t < T; t++) { int M; cin >> M; for (int N = 1; N <= M; N++) { ll s = 0; int maxkk = min(M / 2, M / (N + 1)); for (int k = 1; k <= maxkk; k++) { ll u = (ll)M + N - (ll)k * (N + 1LL); if (u < N) continue; ll val = fact[u] * invfact[u - N] % MOD; s = (s + val) % MOD; } if (N > 1) cout << " "; cout << s; } cout << endl; } return 0; }